import React, { useEffect, useState, useRef, useCallback } from 'react';
import { Link } from 'react-router-dom';

const About = () => {
  const [animatedElements, setAnimatedElements] = useState(new Set());

  const handleElementIntersection = useCallback((entries) => {
    entries.forEach((entry) => {
      if (entry.isIntersecting) {
        const elementId = entry.target.dataset.animateId;
        if (elementId) {
          setAnimatedElements(prev => new Set([...prev, elementId]));
          entry.target.style.opacity = '1';
          entry.target.style.transform = 'translateY(0) translateX(0) scale(1)';
        }
      }
    });
  }, []);

  useEffect(() => {
    const elementObserver = new IntersectionObserver(handleElementIntersection, {
      threshold: 0.1,
      rootMargin: '0px 0px -100px 0px'
    });

    const animatedElements = document.querySelectorAll('[data-animate-id]');
    animatedElements.forEach(element => {
      elementObserver.observe(element);
    });

    return () => elementObserver.disconnect();
  }, [handleElementIntersection]);

  return (
    <div>
      <style jsx>{`
        @keyframes fadeInUp {
          from {
            opacity: 0;
            transform: translateY(30px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        @keyframes fadeInLeft {
          from {
            opacity: 0;
            transform: translateX(-30px);
          }
          to {
            opacity: 1;
            transform: translateX(0);
          }
        }

        @keyframes fadeInRight {
          from {
            opacity: 0;
            transform: translateX(30px);
          }
          to {
            opacity: 1;
            transform: translateX(0);
          }
        }

        .scroll-animate {
          opacity: 0;
          transition: all 0.8s ease-out;
        }

        .scroll-animate.fade-up {
          transform: translateY(30px);
        }

        .scroll-animate.fade-left {
          transform: translateX(-30px);
        }

        .scroll-animate.fade-right {
          transform: translateX(30px);
        }

        .scroll-animate.delay-1 { transition-delay: 0.1s; }
        .scroll-animate.delay-2 { transition-delay: 0.2s; }
        .scroll-animate.delay-3 { transition-delay: 0.3s; }
        .scroll-animate.delay-4 { transition-delay: 0.4s; }
        .scroll-animate.delay-5 { transition-delay: 0.5s; }
        .scroll-animate.delay-6 { transition-delay: 0.6s; }
        .scroll-animate.delay-7 { transition-delay: 0.7s; }

        @media (max-width: 768px) {
          .about-hero h2 {
            font-size: 1.8rem !important;
          }
          .about-hero h3 {
            font-size: 1.4rem !important;
          }
          .about-hero .about-image-container {
            margin-top: 2rem;
          }
        }
      `}</style>

      {/* Hero About Section */}
      <section className="about-hero py-5" style={{ 
        backgroundColor: '#f8f9ff', 
        position: 'relative',
        overflow: 'hidden',
        marginTop: '76px'
      }}>
        <div className="container">
          <div className="row align-items-center">
            {/* Left Content */}
            <div className="col-lg-6 mb-4">
              <div className="about-content">
                <h2 className="fw-bold mb-4 scroll-animate fade-left delay-1" data-animate-id="about-title" style={{ 
                  color: '#1a237e', 
                  fontSize: '2.5rem', 
                  lineHeight: '1.2',
                  marginBottom: '2rem'
                }}>
                  Unlock Your Business's True Potential
                </h2>

                <p className="mb-4 scroll-animate fade-left delay-2" data-animate-id="about-desc1" style={{ 
                  color: '#666', 
                  fontSize: '16px', 
                  lineHeight: '1.6' 
                }}>
                  With <span style={{ color: '#e91e63', fontWeight: '600' }}>GJTecho.com</span> by your side, you can unlock the true potential of your business and take it to the next level with our comprehensive set of digital solutions. We'll help you create an engaging website that will draw in new customers, craft high-quality content to boost online visibility, and leverage social media to increase brand awareness.
                </p>

                <h3 className="fw-bold mb-3 scroll-animate fade-left delay-3" data-animate-id="about-subtitle1" style={{ 
                  color: '#1a237e', 
                  fontSize: '1.8rem' 
                }}>
                  More For Less
                </h3>

                <p className="mb-4 scroll-animate fade-left delay-4" data-animate-id="about-desc2" style={{ 
                  color: '#666', 
                  fontSize: '16px', 
                  lineHeight: '1.6' 
                }}>
                  At GJTecho.com, we believe in delivering <span style={{ color: '#e91e63', fontWeight: '600' }}>more for less</span>. We offer a wide range of IT solutions at prices that are much lower than most other digital agencies but with no compromise on quality or service delivery.
                </p>

                <h3 className="fw-bold mb-3 scroll-animate fade-left delay-5" data-animate-id="about-subtitle2" style={{ 
                  color: '#1a237e', 
                  fontSize: '1.8rem' 
                }}>
                  Achieve Your Goals
                </h3>

                <p className="mb-4 scroll-animate fade-left delay-6" data-animate-id="about-desc3" style={{ 
                  color: '#666', 
                  fontSize: '16px', 
                  lineHeight: '1.6' 
                }}>
                  Our talented team will analyze your goals and develop a tailored strategy that will help you reach them faster and more efficiently. With our advanced technology, we can provide the <span style={{ color: '#e91e63', fontWeight: '600' }}>highest quality of services</span> at a fraction of the cost, saving you time and money in the long run!
                </p>

                <div className="scroll-animate fade-left delay-7" data-animate-id="about-cta">
                  <Link 
                    to="/contact" 
                    className="btn px-4 py-2"
                    style={{
                      background: 'linear-gradient(135deg, #ff7b7b 0%, #ff9a9a 100%)',
                      color: 'white',
                      borderRadius: '25px',
                      fontWeight: '500',
                      textDecoration: 'none',
                      border: 'none',
                      transition: 'all 0.3s ease'
                    }}
                    onMouseEnter={(e) => e.currentTarget.style.transform = 'scale(1.05)'}
                    onMouseLeave={(e) => e.currentTarget.style.transform = 'scale(1)'}
                  >
                    Contact Us â†’
                  </Link>
                </div>
              </div>
            </div>

            {/* Right Content - Image */}
            <div className="col-lg-6 mb-4">
              <div className="about-image-container scroll-animate fade-right delay-3" data-animate-id="about-image" style={{ 
                position: 'relative',
                textAlign: 'center'
              }}>
                <img 
                  src="https://images.unsplash.com/photo-1522071820081-009f0129c71c?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80" 
                  alt="Team working together" 
                  className="img-fluid"
                  style={{ 
                    borderRadius: '20px',
                    maxWidth: '100%',
                    height: 'auto',
                    boxShadow: '0 20px 40px rgba(0,0,0,0.1)'
                  }}
                />

                {/* Decorative Elements */}
                <div style={{
                  position: 'absolute',
                  top: '10%',
                  left: '-10%',
                  width: '80px',
                  height: '80px',
                  background: 'linear-gradient(135deg, #ff7b7b 0%, #ff9a9a 100%)',
                  borderRadius: '50%',
                  opacity: 0.8,
                  zIndex: -1
                }}></div>

                <div style={{
                  position: 'absolute',
                  bottom: '15%',
                  right: '-5%',
                  width: '60px',
                  height: '60px',
                  background: 'linear-gradient(135deg, #7b7bff 0%, #9a9aff 100%)',
                  borderRadius: '50%',
                  opacity: 0.8,
                  zIndex: -1
                }}></div>

                <div style={{
                  position: 'absolute',
                  top: '60%',
                  left: '-5%',
                  width: '40px',
                  height: '40px',
                  background: 'linear-gradient(135deg, #7bff7b 0%, #9aff9a 100%)',
                  borderRadius: '50%',
                  opacity: 0.8,
                  zIndex: -1
                }}></div>
              </div>
            </div>
          </div>
        </div>

        {/* Background Decorative Elements */}
        <div style={{
          position: 'absolute',
          top: '5%',
          right: '5%',
          width: '100px',
          height: '100px',
          background: 'rgba(233, 30, 99, 0.1)',
          borderRadius: '50%',
          zIndex: 0
        }}></div>

        <div style={{
          position: 'absolute',
          bottom: '10%',
          left: '3%',
          width: '120px',
          height: '120px',
          background: 'rgba(26, 35, 126, 0.1)',
          borderRadius: '50%',
          zIndex: 0
        }}></div>
      </section>

    <section className="py-5">
      <div className="container">
        <div className="text-center mb-5">
          <h1 className="fw-bold">About Our Agency</h1>
          <p className="lead">Your trusted digital partner in Vadodara</p>
        </div>

        <div className="row align-items-center mb-5">
          <div className="col-lg-6">
            <h2 className="fw-bold mb-4">Who We Are</h2>
            <p>We are a dynamic digital agency based in Vadodara, specializing in comprehensive digital solutions for businesses worldwide. With a focus on innovation, quality, and client satisfaction, we deliver cutting-edge web development, mobile applications, UI/UX design, and digital marketing services.</p>
            <p>Our small-to-medium sized team handles around 20 projects annually, ensuring personalized attention and quality delivery for each client. We pride ourselves on quick turnaround times, cost-effective solutions, and 24/7 support.</p>
          </div>
          <div className="col-lg-6 text-center">
            <div className="bg-light p-5 rounded">
              <i className="fas fa-users fa-4x text-primary mb-3"></i>
              <h4>Expert Team</h4>
              <p>Dedicated professionals with years of experience</p>
            </div>
          </div>
        </div>

        <div className="row mb-5">
          <div className="col-md-4 mb-4">
            <div className="text-center">
              <i className="fas fa-target fa-3x text-primary mb-3"></i>
              <h5>Our Mission</h5>
              <p>To empower businesses with innovative digital solutions that drive growth and success in the digital landscape.</p>
            </div>
          </div>
          <div className="col-md-4 mb-4">
            <div className="text-center">
              <i className="fas fa-eye fa-3x text-primary mb-3"></i>
              <h5>Our Vision</h5>
              <p>To be the leading digital agency in Gujarat, known for excellence, innovation, and client satisfaction.</p>
            </div>
          </div>
          <div className="col-md-4 mb-4">
            <div className="text-center">
              <i className="fas fa-heart fa-3x text-primary mb-3"></i>
              <h5>Our Values</h5>
              <p>Quality, integrity, innovation, and client-first approach in everything we do.</p>
            </div>
          </div>
        </div>

        <div className="row">
          <div className="col-12">
            <h2 className="fw-bold text-center mb-4">Why Choose Us?</h2>
            <div className="row">
              <div className="col-md-6 mb-3">
                <div className="d-flex">
                  <i className="fas fa-check-circle text-success me-3 mt-1"></i>
                  <div>
                    <h6>Individualized Service</h6>
                    <p className="text-muted mb-0">Personalized attention for every project</p>
                  </div>
                </div>
              </div>
              <div className="col-md-6 mb-3">
                <div className="d-flex">
                  <i className="fas fa-check-circle text-success me-3 mt-1"></i>
                  <div>
                    <h6>Cost-Effective Pricing</h6>
                    <p className="text-muted mb-0">Competitive rates without compromising quality</p>
                  </div>
                </div>
              </div>
              <div className="col-md-6 mb-3">
                <div className="d-flex">
                  <i className="fas fa-check-circle text-success me-3 mt-1"></i>
                  <div>
                    <h6>Quick Turnaround</h6>
                    <p className="text-muted mb-0">Fast delivery within agreed timelines</p>
                  </div>
                </div>
              </div>
              <div className="col-md-6 mb-3">
                <div className="d-flex">
                  <i className="fas fa-check-circle text-success me-3 mt-1"></i>
                  <div>
                    <h6>24/7 Support</h6>
                    <p className="text-muted mb-0">Round-the-clock assistance when you need it</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      <section className="py-5 bg-light mt-5">
        <div className="container text-center">
          <h2 className="fw-bold mb-4">Ready to Work Together?</h2>
          <p className="lead mb-4">Let's discuss how we can help transform your digital presence.</p>
          <Link to="/contact" className="btn btn-primary btn-lg">Get Started Today</Link>
        </div>
      </section>
    </section>
    </div>
  );
};

export default About;
