import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';

const Portfolio = () => {
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  const projects = [
    {
      title: 'Graphic Design Portfolio',
      category: 'Graphic Design',
      image: 'https://images.unsplash.com/photo-1626785774573-4b799315345d?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80',
      slug: 'graphic-design',
      projectLink: 'https://behance.net/yourusername/graphic-design'
    },
    {
      title: 'Personal Portfolio Web Development',
      category: 'Website Development',
      image: 'https://images.unsplash.com/photo-1555066931-4365d14bab8c?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80',
      slug: 'personal-portfolio',
      projectLink: 'https://vishal1183-bca.github.io/Personal-Portfolio-Website/'
    },
    {
      title: 'Udhar Book App Development',
      category: 'Mobile Development',
      image: 'https://images.unsplash.com/photo-1512941937669-90a1b58e7e9c?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80',
      slug: 'udhar-book-app',
      projectLink: 'https://github.com/yourusername/udhar-book-app'
    },
    {
      title: 'E-commerce Web Development',
      category: 'Website Development',
      image: 'https://images.unsplash.com/photo-1556742049-0cfed4f6a45d?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80',
      slug: 'ecommerce-web',
      projectLink: 'https://your-ecommerce-project.com'
    },
    {
      title: 'Hotel Management Desktop Application',
      category: 'Desktop Application',
      image: 'https://images.unsplash.com/photo-1551434678-e076c223a692?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80',
      slug: 'hotel-management',
      projectLink: 'https://github.com/yourusername/hotel-management'
    },
    {
      title: 'Restaurant Management Desktop',
      category: 'Desktop Application',
      image: 'https://images.unsplash.com/photo-1517248135467-4c7edcad34c4?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80',
      slug: 'restaurant-management',
      projectLink: 'https://github.com/yourusername/restaurant-management'
    },
    {
      title: 'Expense Tracker Java Development',
      category: 'Desktop Application',
      image: 'https://images.unsplash.com/photo-1554224155-6726b3ff858f?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80',
      slug: 'expense-tracker',
      projectLink: 'https://github.com/yourusername/expense-tracker'
    }
  ];

  return (
    <div className="portfolio-page">
      <style jsx>{`
        @keyframes float {
          0%, 100% { transform: translateY(0px); }
          50% { transform: translateY(-20px); }
        }

        @keyframes pulse {
          0%, 100% { transform: scale(1); }
          50% { transform: scale(1.05); }
        }

        @keyframes rotate {
          from { transform: rotate(0deg); }
          to { transform: rotate(360deg); }
        }

        @keyframes gradientShift {
          0% { background-position: 0% 50%; }
          50% { background-position: 100% 50%; }
          100% { background-position: 0% 50%; }
        }

        @keyframes particleFloat {
          0%, 100% { transform: translateY(0px) translateX(0px); }
          25% { transform: translateY(-30px) translateX(10px); }
          50% { transform: translateY(-60px) translateX(-5px); }
          75% { transform: translateY(-30px) translateX(-10px); }
        }

        @keyframes shimmer {
          0% { background-position: -200% 0; }
          100% { background-position: 200% 0; }
        }

        .gradient-bg {
          background: linear-gradient(-45deg, #667eea, #764ba2, #f093fb, #f5576c);
          background-size: 400% 400%;
          animation: gradientShift 15s ease infinite;
        }

        .particle {
          position: absolute;
          background: rgba(255, 255, 255, 0.1);
          border-radius: 50%;
          animation: particleFloat 8s ease-in-out infinite;
        }

        .floating-element {
          animation: float 6s ease-in-out infinite;
        }

        .pulse-element {
          animation: pulse 4s ease-in-out infinite;
        }

        .rotating-element {
          animation: rotate 20s linear infinite;
        }

        .shimmer-text {
          background: linear-gradient(90deg, #667eea 25%, #764ba2 50%, #667eea 75%);
          background-size: 200% 100%;
          background-clip: text;
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
          animation: shimmer 3s ease-in-out infinite;
        }



        .hover-lift {
          transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
        }

        .hover-lift:hover {
          transform: translateY(-15px) scale(1.02);
          box-shadow: 0 25px 50px rgba(0,0,0,0.2) !important;
        }

        .glass-effect {
          background: rgba(255, 255, 255, 0.1);
          backdrop-filter: blur(10px);
          border: 1px solid rgba(255, 255, 255, 0.2);
        }

        @media (max-width: 768px) {
          .portfolio-grid {
            grid-template-columns: 1fr !important;
            gap: 2rem !important;
          }
          .portfolio-card {
            max-width: 100% !important;
          }
          .portfolio-title {
            font-size: 2rem !important;
          }
        }

        @media (max-width: 576px) {
          .portfolio-title {
            font-size: 1.5rem !important;
          }
          .portfolio-card {
            padding: 1rem !important;
          }
        }
      `}</style>

      <section className="py-5 gradient-bg" style={{ 
        marginTop: '76px', 
        position: 'relative',
        overflow: 'hidden',
        minHeight: '100vh'
      }}>
        {/* Animated Particles Background */}
        <div style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          overflow: 'hidden',
          zIndex: 0
        }}>
          {[...Array(15)].map((_, i) => (
            <div
              key={i}
              className="particle"
              style={{
                width: `${Math.random() * 8 + 4}px`,
                height: `${Math.random() * 8 + 4}px`,
                left: `${Math.random() * 100}%`,
                top: `${Math.random() * 100}%`,
                animationDelay: `${Math.random() * 8}s`,
                animationDuration: `${Math.random() * 10 + 8}s`
              }}
            />
          ))}
        </div>

        {/* Glass Overlay */}
        <div className="glass-effect" style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          zIndex: 1
        }} />

        {/* Floating Decorative Elements */}
        <div className="floating-element" style={{
          position: 'absolute',
          top: '10%',
          right: '5%',
          width: '100px',
          height: '100px',
          background: 'linear-gradient(45deg, rgba(255,255,255,0.1), rgba(255,255,255,0.05))',
          borderRadius: '50%',
          zIndex: 1,
          border: '2px dashed rgba(255,255,255,0.2)'
        }} />

        <div className="pulse-element" style={{
          position: 'absolute',
          bottom: '15%',
          left: '3%',
          width: '80px',
          height: '80px',
          background: 'linear-gradient(45deg, rgba(255,255,255,0.08), rgba(255,255,255,0.03))',
          borderRadius: '30% 70% 70% 30% / 30% 30% 70% 70%',
          zIndex: 1,
          animationDelay: '2s'
        }} />
        <div className="container" style={{ position: 'relative', zIndex: 2 }}>
          <div className="text-center mb-5">
            <p className="text-uppercase fw-bold mb-2" style={{ 
              color: 'rgba(255,255,255,0.9)', 
              fontSize: '14px', 
              letterSpacing: '2px',
              textShadow: '0 2px 4px rgba(0,0,0,0.3)'
            }}>PORTFOLIO</p>
            <h1 className="fw-bold portfolio-title shimmer-text" style={{ 
              fontSize: '2.5rem',
              marginBottom: '1rem',
              textShadow: '0 4px 8px rgba(0,0,0,0.3)'
            }}>
              Our Creative <span style={{ color: '#fff' }}>Showcase</span>
            </h1>
            <p style={{
              color: 'rgba(255,255,255,0.8)',
              fontSize: '18px',
              maxWidth: '600px',
              margin: '0 auto',
              textShadow: '0 2px 4px rgba(0,0,0,0.2)'
            }}>
              Discover our latest projects and creative solutions that bring ideas to life
            </p>
          </div>

          <div className="portfolio-grid" style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(350px, 1fr))',
            gap: '3rem',
            maxWidth: '1200px',
            margin: '0 auto'
          }}>
            {projects.map((project, index) => (
              <div 
                key={index} 
                className={`portfolio-card scroll-animate scale hover-lift delay-${Math.min(index + 4, 7)}`}
                data-animate-id={`project-${index}`}
                style={{
                  background: 'linear-gradient(135deg, rgba(255,255,255,0.95) 0%, rgba(255,255,255,0.9) 100%)',
                  borderRadius: '25px',
                  overflow: 'hidden',
                  boxShadow: '0 15px 35px rgba(0,0,0,0.1)',
                  maxWidth: '400px',
                  margin: '0 auto',
                  border: '1px solid rgba(255,255,255,0.2)',
                  backdropFilter: 'blur(10px)',
                  opacity: 0,
                  transform: 'scale(0.8) translateY(30px)'
                }}>
                <div style={{ position: 'relative', overflow: 'hidden' }}>
                  <img 
                    src={project.image}
                    alt={project.title}
                    style={{
                      width: '100%',
                      height: '250px',
                      objectFit: 'cover',
                      transition: 'transform 0.4s ease'
                    }}
                    onMouseEnter={(e) => e.target.style.transform = 'scale(1.1)'}
                    onMouseLeave={(e) => e.target.style.transform = 'scale(1)'}
                  />
                  {/* Overlay Effect */}
                  <div style={{
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    right: 0,
                    bottom: 0,
                    background: 'linear-gradient(45deg, rgba(102, 126, 234, 0.1), rgba(118, 75, 162, 0.1))',
                    opacity: 0,
                    transition: 'opacity 0.3s ease'
                  }}
                  onMouseEnter={(e) => e.target.style.opacity = '1'}
                  onMouseLeave={(e) => e.target.style.opacity = '0'}
                  />
                </div>

                <div style={{ padding: '2rem', position: 'relative' }}>
                  <h4 className="fw-bold mb-2" style={{ color: '#1a237e', fontSize: '1.2rem', lineHeight: '1.3' }}>
                    {project.title}
                  </h4>
                  <span className="mb-3 d-inline-block" style={{ 
                    color: '#667eea', 
                    fontSize: '12px',
                    background: 'linear-gradient(135deg, rgba(102, 126, 234, 0.1), rgba(118, 75, 162, 0.1))',
                    padding: '4px 12px',
                    borderRadius: '15px',
                    fontWeight: '500',
                    textTransform: 'uppercase',
                    letterSpacing: '0.5px'
                  }}>
                    {project.category}
                  </span>

                  <div className="d-flex gap-2 align-items-center justify-content-start">
                    <Link 
                      to={`/portfolio/${project.slug}`}
                      className="btn"
                      style={{
                        background: 'linear-gradient(135deg, rgba(255,255,255,0.2), rgba(255,255,255,0.1))',
                        color: '#667eea',
                        border: '2px solid rgba(102, 126, 234, 0.3)',
                        borderRadius: '25px',
                        padding: '8px 20px',
                        fontSize: '14px',
                        fontWeight: '500',
                        transition: 'all 0.4s ease',
                        textDecoration: 'none',
                        display: 'inline-block',
                        backdropFilter: 'blur(10px)'
                      }}
                      onMouseEnter={(e) => {
                        e.target.style.background = 'linear-gradient(135deg, #667eea, #764ba2)';
                        e.target.style.color = 'white';
                        e.target.style.transform = 'translateY(-3px) scale(1.05)';
                        e.target.style.boxShadow = '0 10px 25px rgba(102, 126, 234, 0.4)';
                      }}
                      onMouseLeave={(e) => {
                        e.target.style.background = 'linear-gradient(135deg, rgba(255,255,255,0.2), rgba(255,255,255,0.1))';
                        e.target.style.color = '#667eea';
                        e.target.style.transform = 'translateY(0) scale(1)';
                        e.target.style.boxShadow = 'none';
                      }}
                    >
                      View Details
                    </Link>

                    <a 
                      href={project.projectLink}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="btn"
                      style={{
                        background: 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)',
                        color: 'white',
                        border: 'none',
                        borderRadius: '25px',
                        padding: '8px 20px',
                        fontSize: '14px',
                        fontWeight: '500',
                        transition: 'all 0.4s ease',
                        textDecoration: 'none',
                        display: 'inline-block',
                        boxShadow: '0 4px 15px rgba(240, 147, 251, 0.3)'
                      }}
                      onMouseEnter={(e) => {
                        e.target.style.transform = 'translateY(-2px) scale(1.05)';
                        e.target.style.boxShadow = '0 8px 25px rgba(240, 147, 251, 0.4)';
                      }}
                      onMouseLeave={(e) => {
                        e.target.style.transform = 'translateY(0) scale(1)';
                        e.target.style.boxShadow = '0 4px 15px rgba(240, 147, 251, 0.3)';
                      }}
                    >
                      <i className="fas fa-external-link-alt me-2"></i>Live Demo
                    </a>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>
    </div>
  );
};

export default Portfolio;
